class FbLogin extends StatefulWidget {


  @override
  _FbLoginState createState() => _FbLoginState();
}

class _FbLoginState extends State<FbLogin> {

  Map? _userData;
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      home: Scaffold(
        appBar: AppBar(
          title: Text('Facebook (Logged ' + (_userData == null ? 'Out' : 'In') + ')'),
          centerTitle: true,
          leading: IconButton(
            icon: Icon(Icons.arrow_back, color: Colors.white),
            onPressed: () => Navigator.of(context).pop(),
          ),
        ),
        // body: loginUi(),
        body: Center(
          child: Column(
            children: [
              ElevatedButton(child: Text('Log In'),
                  onPressed: () async {

                    final result = await FacebookAuth.i.login(
                        permissions: ["public_profile", "email"]
                    );

                    if (result.status == LoginStatus.success) {

                      final userData = await FacebookAuth.i.getUserData(
                        fields: "email,name",
                      );

                      setState(() {
                        _userData = userData;
                      });

                      print("FB :"+_userData.toString());

                    }

                  }),
              Padding(
                padding: const EdgeInsets.all(28.0),
                child: ElevatedButton(child: Text('Log Out'),
                    onPressed: () async {
                      await FacebookAuth.i.logOut();

                      setState(() {
                        _userData = null;
                      });
                    }),
              ),
            ],
          ),
        ),
      ),

    );

  }
